mapper.select:
select *
from (
select rownum
as rn, A.*
from (
select * from
product
) A
)
where rn between #{start} and #{end}

select file_name from p_attach
where p_id=#{p_id}
order by file_id

mapper.update:
UPDATE product
SET p_hit = p_hit + 1
WHERE p_id = #{p_id}

mapper.select:
SELECT COUNT(*)
FROM review
WHERE p_id = #{p_id}
