mapper.select:
select *
from (
select rownum
as rn, A.*
from (
select * from
product
) A
)
where rn between #{start} and #{end}

select DISTINCT ctg_big
from ctg
where ctg_b_no=#{ctg_b_no}

select p_id, p_name, p_price, p_img1, p_delivery, p.p_cate, ctg_small, d_info, (SELECT p_id FROM zzim WHERE userid=#{userid} AND p_id=p.p_id) p_like from product p, ctg c, delivery d where p_cate = ctg_s_no and p_delivery = d_num and p_cate = #{no}
order by p_hit desc
order by p_date desc
order by p_sell desc
order by p_price
limit #{pageCnt} offset #{start}
